contracts/Swapper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract Swapper is
    OwnableUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable
{
    using SafeERC20 for IERC20;

    struct SwapDescription {
        address srcToken;
        address dstToken;
        uint256 amount;
        bytes data;
    }

    address public constant NATIVE_TOKEN =
        0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    address public newExchange =0xDef1C0ded9bec7F1a1670819833240f027b25EfF;
    address public  swapAdmin;
    uint256 public swapFeePercentage; 

    event Swapped(
        address indexed user,
        address indexed inputToken,
        address indexed outputToken,
        uint256 inputAmount,
        uint256 outputAmount,
        uint256 fee
    );

    event AdminAdded(address indexed admin);
    event AdminRemoved(address indexed admin);
    event SwapFeeUpdated(uint256 newFeePercentage);

    modifier onlyAdmin() {
        require( msg.sender == swapAdmin , "Caller is not an admin");
        _;
    }

    function initialize(address _swapAdmin)
        external
        initializer
    {
        __Ownable_init(msg.sender);
        __Pausable_init();
        __ReentrancyGuard_init();  
        __UUPSUpgradeable_init();
        swapFeePercentage = 50; // Default fee is 0.5%
        swapAdmin = _swapAdmin;
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyOwner
    {}

    receive() external payable {}

    function swap(bytes calldata _data, address _swapper)
        external
        payable
        whenNotPaused
        nonReentrant
    {
        SwapDescription memory swapDesc = _decode(_data);
        require(swapDesc.amount != 0, "amount == 0");
        require(
            swapDesc.srcToken != address(0) && swapDesc.dstToken != address(0),
            "invalid tokens"
        );
        require(
            swapDesc.srcToken != swapDesc.dstToken,
            "src token == dst token"
        );
        if (msg.sender != address(this)) {
            require(msg.sender == _swapper, "caller != swapper");
        }
        if (swapDesc.srcToken == NATIVE_TOKEN) {
            require(msg.value == swapDesc.amount, "msg.value != amount");
        } else {
            require(msg.value == 0, "msg.value != 0");
            require(
                swapDesc.amount <=
                    IERC20(swapDesc.srcToken).balanceOf(_swapper),
                "insufficient balance"
            );
            _transferFrom(swapDesc.srcToken, _swapper, swapDesc.amount);
            _approve(swapDesc.srcToken, newExchange, swapDesc.amount);
        }

        uint256 swapOutput = _swap(swapDesc);
        uint256 fee = (swapOutput * swapFeePercentage) / 10000;

        if (swapDesc.dstToken == NATIVE_TOKEN) {
            payable( swapAdmin).transfer(fee);
            swapOutput -= fee;
            payable(_swapper).transfer(swapOutput);
        } else {
            _transfer(swapDesc.dstToken,  swapAdmin, fee);
            swapOutput -= fee;
            _transfer(swapDesc.dstToken, _swapper, swapOutput);
        }

        emit Swapped(
            _swapper,
            swapDesc.srcToken,
            swapDesc.dstToken,
            swapDesc.amount,
            swapOutput,
            fee
        );
    }

    function updateSwapFee(uint256 _newFeePercentage) external onlyOwner {
        // require(
        //     _newFeePercentage <= 1000,
        //     "Fee percentage cannot exceed 10%" //  limit
        // );
        swapFeePercentage = _newFeePercentage;
        emit SwapFeeUpdated(_newFeePercentage);
    }

    function getBalance(address _token) public view returns (uint256) {
        if (_token == NATIVE_TOKEN) {
            return address(this).balance;
        }
        return IERC20(_token).balanceOf(address(this));
    }

    function addAdmin(address _admin) external onlyOwner {
        require(_admin != address(0), "Invalid admin address");
        swapAdmin = _admin;
        emit AdminAdded(_admin);
    }

    function setNewExchange(address _newExchange) external onlyOwner {
        require(_newExchange != address(0), "zero address params");
        newExchange = _newExchange;
    }


    function _decode(bytes calldata _data)
        private
        pure
        returns (SwapDescription memory)
    {
        (address a, address b, uint256 c, bytes memory d) = abi.decode(
            _data,
            (address, address, uint256, bytes)
        );
        return SwapDescription(a, b, c, d);
    }

    function _swap(SwapDescription memory _swapDesc) private returns (uint256) {
        uint256 balanceBeforeSwap = getBalance(_swapDesc.dstToken);
        (bool success, ) = newExchange.call{value: msg.value}(_swapDesc.data);
        require(success, "swap failed");
        uint256 balanceAfterSwap = getBalance(_swapDesc.dstToken);
        uint256 swapOutput = balanceAfterSwap - balanceBeforeSwap;
        assert(swapOutput != 0);
        return swapOutput;
    }

    function _approve(
        address _token,
        address _spender,
        uint256 _amount
    ) private {
        uint256 allowance = IERC20(_token).allowance(address(this), _spender);
        if (allowance < _amount) {

            IERC20(_token).approve(_spender, _amount);
        }
    }

    function _transfer(
        address _token,
        address _to,
        uint256 _amount
    ) private {
        IERC20(_token).safeTransfer(_to, _amount);
    }

    function _transferFrom(
        address _token,
        address _from,
        uint256 _amount
    ) private {
        IERC20(_token).safeTransferFrom(_from, address(this), _amount);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

}

